import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';part 'catalog_child_event.dart';part 'catalog_child_state.dart';class CatalogChildBloc extends Bloc<CatalogChildEvent, CatalogChildState> {  CatalogChildBloc() : super(CatalogChildInitial()) {    on<ClickItemForSlugEvent>((event, emit) {      emit(ClickItemForSlugState(event.title, event.slug));    });  }}