part of 'home_bloc.dart';@immutableabstract class HomeState {}class HomeInitial extends HomeState {}class GetSliderState extends HomeState {  final SliderModel data;  GetSliderState(this.data);}class GetAllCategoriesState extends HomeState {  final AllCategoriesModel data;  GetAllCategoriesState(this.data);}class GetAllSpecialState extends HomeState{  final SpecialModel data;  GetAllSpecialState(this.data);}class GetProductsState extends HomeState{  final TopProductsModel data;  GetProductsState(this.data);}class GetAllBrandsState extends HomeState{  final BrandsModel data;  GetAllBrandsState(this.data);}class GetALlNEwProductState extends HomeState{  final NewProductModel data;  GetALlNEwProductState(this.data);}class GetAllFavouriteState extends HomeState {  final List<ItemHolder> data;  GetAllFavouriteState(this.data);}class LikeProductState extends HomeState{  LikeProductState(void likeProduct);}class GetAllSavedHomeState extends HomeState {  final List<ItemHolder> data;  GetAllSavedHomeState(this.data);}class SaveProductState extends HomeState{  SaveProductState(void saveProduct);}class LoadingState extends HomeState {}