import 'package:hive/hive.dart';part 'item_holder.g.dart';@HiveType(typeId: 0)class ItemHolder extends HiveObject {  @HiveField(0)  String id;  @HiveField(1)  String name;  @HiveField(2)  String image;  @HiveField(3)  String value;  @HiveField(4)  String price;  @HiveField(5)  String count;  @HiveField(6)  bool favourite;  @HiveField(7)  bool isSaved;  ItemHolder(this.id,this.name,this.image, this.value,this.price, this.count, this.favourite, this.isSaved);}